import React, { Component } from "react";
import { connect } from 'react-redux';

// import actions
import { logout } from '../../redux/actions/auth';

class UserProfile extends Component {
	constructor(props) {
		super(props);

		this.toggleShow = this.toggleShow.bind(this);
		this.state = {
			show: false,
		};
	}

	toggleShow() {
		this.setState({ show: !this.state.show })
	}

	render() {
		return (
			<div className="UserProfile">
				{
					this.props.auth.user.length === 0 ?
						<div className="User">
							<div className="name">Login</div>
						</div>
						:
						<div>
							<div className="User" onClick={this.toggleShow}>
								<div className="name">{this.props.auth.user[0].name}</div>
								<div className="image"><img src="https://upload.wikimedia.org/wikipedia/commons/7/70/User_icon_BLACK-01.png" /></div>
							</div>
							{
								this.state.show ?
								<div className="UserProfile-menu" id="user-account" style={{ display: "block" }}>
									<div className="UserProfileSwitch">
										<ul>
											<li>
												<div className="UserProfile-image">
													<img src="https://upload.wikimedia.org/wikipedia/commons/7/70/User_icon_BLACK-01.png" />
												</div>
												{/* <div style={{ textTransform: 'capitalize' }}>{this.props.auth.user[0].name}</div> */}
											</li>
										</ul>
									</div>
									<div className="UserNavigation">
										<ul>
											<li>Your Account</li>
											<li>Help Center</li>
											<li>Sign out</li>
										</ul>
									</div>
								</div>
								:
								null
								}

						</div>
				}
			</div>
		);
	}
}

const mapsStateToProps = (state) => ({
	auth: state.auth
})

export default connect(mapsStateToProps)(UserProfile);
