import React, { Component } from "react";
import { connect } from 'react-redux';

// import actions
import { logout } from '../../redux/actions/auth';

class UserProfile extends Component {
	render() {
		return (
			<div className="UserProfile">
				{
					this.props.auth.user.length === 0 ?
						<div className="User">
							<div className="name">Login</div>
						</div>
						:
						<div>
							<div className="User">
								{/* <div className="name">{this.props.auth.user[0].name}</div> */}
								<div className="image"><img src="http://lorempixel.com/96/96/" /></div>
							</div>
							<div className="UserProfile-menu">
								<div className="UserProfileSwitch">
									<ul>
										<li>
											<div className="UserProfile-image">
												<img src="http://lorempixel.com/96/96" />
											</div>
											<div className="UserProfile-name">{this.props.auth.user[0].name}</div>
										</li>
									</ul>
								</div>
								<div className="UserNavigation">
									<ul>
										<li>Sign out</li>
									</ul>
								</div>
							</div>
						</div>
				}
			</div>
						);
					}
				}

const mapsStateToProps = (state) => ({
					auth: state.auth
})

export default connect(mapsStateToProps)(UserProfile);
